summary.note:
This converter uses the Newtonsoft JSON.NET
package so you can decorate your data types with attributes such as [JsonProperty],
[JsonIgnore],... to control how your data is serialized.

remarks.code:
public class MyWorkflow : WorkflowBase
{
    public async Task DoSomething()
    {
        var activities = Workflow.NewActivityStub<MyActivities>();

        await activities.ActivityAAsync();  
        await activities.ActivityBAsync();  
    }
}

public class MyWorkflow : WorkflowBase
{
    public async Task<byte[]> RunAsync(byte[] args)
    {
        var activities = Workflow.NewActivityStub<MyActivities>();

        await activities.ActivityCAsync();  
        await activities.ActivityBAsync();
    }
}

public class MyWorkflow : WorkflowBase
{
    public async Task<byte[]> RunAsync(byte[] args)
    {
        var activities = Workflow.NewActivityStub<MyActivities>();
        var version    = await GetVersionAsync("Replace ActivityA", DefaultVersion, 1);    

        switch (version)
        {
            case DefaultVersion:

                await activities.ActivityAAsync();  
                break;

            case 1:

                await activities.ActivityCAsync();  // <-- change
                break;
        }

        await activities.ActivityBAsync();  
    }
}

public class MyWorkflow : WorkflowBase
{
    public async Task<byte[]> RunAsync(byte[] args)
    {
        var activities = Workflow.NewActivityStub<MyActivities>();
        var version    = await GetVersionAsync("Replace ActivityA", DefaultVersion, 1);    

        switch (version)
        {
            case DefaultVersion:

                await activities.ActivityAAsync();  
                break;

            case 1:

                await activities.ActivityCAsync();  // <-- change
                break;
        }

        version = await GetVersionAsync("Replace ActivityB", 1, 2);    

        switch (version)
        {
            case DefaultVersion:
            case 1:

                await activities.ActivityBAsync();
                break;

            case 2:

                await activities.ActivityDAsync();  // <-- change
                break;
        }
    }
}

public interface IMyWorkflow : IWorkflow
{
    [WorkflowMethod]
    Task MainAsync();

    [WorkflowMethod(Name = "child")]
    Task<string> ChildAsync(string arg);
}

public class MyWorkflow : WorkflowBase, IMyWorkflow
{
    public Task MainAsync()
    {
        var stub1     = Workflow.NewChildWorkflowStub<IMyWorkflow>("FOO");
        var childTask = stub1.DoChildWorkflow();
        var stub2     = Workflow.NewChildWorkflowStub<IMyWorkflow>();
        var value2    = await stub2.DoChildWorkflow("BAR");
        var value1    = await childTask;
    }

    public Task<string> ChildAsync(string arg)
    {
        return await Task.FromResult(arg);
    }
}

public interface IMyWorkflow : IWorkflow
{
    [WorkflowMethod]
    Task MainAsync();

    [WorkflowMethod(Name = "child")]
    Task<string> ChildAsync(string arg);
}

public class MyWorkflow : WorkflowBase, IMyWorkflow
{
    public Task MainAsync()
    {
        var stub1  = Workflow.NewChildWorkflowFutureStub<IMyWorkflow>("child");
        var future = await stub1.StartAsync$lt;string>("FOO");   // Starting the child with param: "FOO"
        var stub2  = Workflow.NewChildWorkflowStub<IMyWorkflow>();
        var value2 = await stub2.DoChildWorkflow("BAR");            // This returns: "BAR"
        var value1 = await future.GetAsync();                       // This returns: "FOO"
    }

    public Task<string> ChildAsync(string arg)
    {
        return await Task.FromResult(arg);
    }
}

public interface IMyActivity : IActivity
{
    [ActivityMethod(Name = "activity-1"]
    Task<string> FooActivityAsync(string arg);

    [ActivityMethod(Name = "activity-2"]
    Task<string> BarActivityAsync(string arg);
}

public MyActivity : ActivityBase, IMyActivity
{
    public Task<string> FooActivityAsync(string arg)
    {
        await Task.FromResult(arg);
    }

    public Task<string> BarActivityAsync(string arg)
    {
        await Task.FromResult(arg);
    }
}

public class MyWorkflow : WorkflowBase, IMyWorkflow
{
    [WorkflowMethod]
    public Task MainAsync()
    {
        var stub     = Workflow.NewActivityStub<IMyActivity>();
        var fooTask  = stub.FooActivity("FOO");
        var barValue = await stub.BarActivityAsync("BAR");
        var fooValue = await fooTask;
    }
}

